import React, { useState } from 'react';

const Search = () => {
  const [authorName, setAuthorName] = useState('');
  const [articles, setArticles] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  // Helper function to fetch article details
  const fetchArticleDetails = async (pmcid) => {
    const proxyUrl = 'https://cors-anywhere.herokuapp.com/';
    const apiKey = 'a72e2410b259ec3b646175c1aa0e1f13eb08'; // Use your actual API key here
    const detailsUrl = `https://eutils.ncbi.nlm.nih.gov/entrez/eutils/efetch.fcgi?db=pmc&id=${pmcid}&api_key=${apiKey}`;
    const apiUrl = proxyUrl + detailsUrl;

    try {
      const response = await fetch(apiUrl);
      const text = await response.text();
      const parser = new DOMParser();
      const xmlDoc = parser.parseFromString(text, "application/xml");

      // Debugging log: output the xmlDoc to console
      console.log(xmlDoc);

      const articleTitleElement = xmlDoc.querySelector("article-title");
      const citationElement = xmlDoc.querySelector("citation");

      if (!articleTitleElement || !citationElement) {
        console.error(`One of the elements was not found in the XML for PMCID: ${pmcid}`);
        return {
          pmcid,
          title: 'Title not found',
          citation: 'Citation not found',
          downloadUrl: `https://www.ncbi.nlm.nih.gov/pmc/articles/PMC${pmcid}/pdf/`
        };
      }

      const articleTitle = articleTitleElement.textContent;
      const citation = citationElement.textContent;

      return {
        pmcid,
        title: articleTitle,
        citation,
        downloadUrl: `https://www.ncbi.nlm.nih.gov/pmc/articles/PMC${pmcid}/pdf/`
      };
    } catch (error) {
      console.error(`Error fetching details for PMCID ${pmcid}: `, error);
      return {
        pmcid,
        title: 'Error fetching title',
        citation: 'Error fetching citation',
        downloadUrl: `https://www.ncbi.nlm.nih.gov/pmc/articles/PMC${pmcid}/pdf/`
      };
    }
  };

  // Function to initiate article search
  const searchArticlesByAuthor = async () => {
    setError('');
    setLoading(true);

    const proxyUrl = 'https://cors-anywhere.herokuapp.com/';
    const query = encodeURIComponent(`${authorName.trim()}[AUTH]`);
    const apiKey = 'a72e2410b259ec3b646175c1aa0e1f13eb08'; // Use your actual API key here
    let fetchedArticles = [];

    const fetchPage = async (retstart) => {
      const searchUrl = `https://eutils.ncbi.nlm.nih.gov/entrez/eutils/esearch.fcgi?db=pmc&term=${query}&retmax=20&retstart=${retstart}&usehistory=y&api_key=${apiKey}`;
      const apiUrl = proxyUrl + searchUrl;
      const response = await fetch(apiUrl);
      const text = await response.text();
      const parser = new DOMParser();
      const xmlDoc = parser.parseFromString(text, "application/xml");
      const ids = Array.from(xmlDoc.querySelectorAll('Id')).map(id => id.textContent);

      // Fetch details for each article ID
      for (const pmcid of ids) {
        const details = await fetchArticleDetails(pmcid);
        fetchedArticles.push(details);
      }

      // Check if there are more articles to fetch
      if (ids.length === 20) {
        await fetchPage(retstart + 20);
      }
    };

    await fetchPage(0); // Start with the first page
    setArticles(fetchedArticles);
    setLoading(false);
  };

  const handleDownloadClick = async (article) => {
    setLoading(true);
    const proxyUrl = 'https://cors-anywhere.herokuapp.com/';
    const response = await fetch(proxyUrl + article.downloadUrl);

    if (!response.ok) {
      setError(`Download failed: HTTP error! Status: ${response.status}`);
      setLoading(false);
      return;
    }

    const blob = await response.blob();
    const url = window.URL.createObjectURL(new Blob([blob]));
    const link = document.createElement('a');
    link.href = url;
    const fileName = `${article.title}-${article.citation}`.replace(/[/\\?%*:|"<>]/g, '-'); // Sanitize the filename
    link.setAttribute('download', `${fileName}.pdf`);
    document.body.appendChild(link);
    link.click();
    link.parentNode.removeChild(link);
    window.URL.revokeObjectURL(url);
    setLoading(false);
  };

  const handleDownloadAllClick = () => {
    articles.forEach((article, index) => {
      setTimeout(() => {
        handleDownloadClick(article);
      }, index * 1000); // Set a delay to prevent rate limit issues
    });
  };

  const listItemStyle = {
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
    fontSize: '14px',
    padding: '5px 0', 
};
  const citationStyle = {
    flexGrow: 1,
    marginRight: '10px', 
    fontSize: '0.8rem',
};
  const buttonStyle = {
    padding: '5px 10px', 
    fontSize: '0.8rem', 
};
  const searchButtonStyle = {
    marginRight: '30px',
};
  const downloadAllStyle = {
    marginRight: '30px',
};
  const unlockAccess = () => {
    window.open('https://cors-anywhere.herokuapp.com/corsdemo', '_blank');
};

  return (
    <div className="search-container">
      <input
	  type="text"
	  value={authorName}
	  onChange={(e) => setAuthorName(e.target.value)}
	  placeholder="Enter author's name"
      />
      <button style={searchButtonStyle} onClick={searchArticlesByAuthor}>Search</button>

      <button style={downloadAllStyle} onClick={handleDownloadAllClick} disabled={articles.length === 0}>
	  Download All 
      </button>
      <button onClick={unlockAccess}>Click here to access</button>

      <ul className="articles-list">
	  {articles.map((article, index)=>(
	    <li key={index} style={listItemStyle}>
	      <span>{article.citation}</span>
	        <button onClick={() => handleDownloadClick(article.pmcid, article.citation)}>
		  Download PDF
		</button>
	      </li>

	  ))}
	</ul>
  </div>
  );
};


export default Search;
